my-page.ts.content:
import {LitElement, html} from 'lit';
import {customElement} from 'lit/decorators.js';

import './my-header.js';
import './my-article.js';
import './my-footer.js';

@customElement('my-page')
class MyPage extends LitElement {
  render() {
    return html`
      <my-header></my-header>
      <my-article></my-article>
      <my-footer></my-footer>
    `;
  }
}

my-article.ts.content:
import {LitElement, css, html} from 'lit';
import {customElement} from 'lit/decorators.js';

@customElement('my-article')
class MyArticle extends LitElement {
  static styles =
    css`
      article {
        padding: 20px;
        font-family: 'Open Sans', sans-serif;
      }
    `
  render() {
    return html`
      <article>
        <p>
          Manage complexity by building large, complex components
          out of smaller, simpler components that do one thing well.
        </p>
      </article>
    `;
  }
}

my-footer.ts.content:
import {LitElement, css, html} from 'lit';
import {customElement} from 'lit/decorators.js';

@customElement('my-footer')
class MyFooter extends LitElement {
  static styles =
    css`
      :host {
      	position: absolute;
      	bottom: 0;
      	left: 0;
      	right: 0;
      }
      footer {
      	display: flex;
      	justify-content: center;
        font-family: Manrope, sans-serif;
      }
    `;
  render() {
    return html`
      <footer><p>THE END</p></footer>
    `;
  }
}

my-header.ts.content:
import {LitElement, css, html} from 'lit';
import {customElement} from 'lit/decorators.js';

@customElement('my-header')
class MyHeader extends LitElement {
  static styles =
    css`
      h1 {
        font-family: Manrope, sans-serif;
        text-align: center;
      }
    `;
  render() {
    return html`
      <header><h1>Element composition</h1></header>
    `;
  }
}
