import {LitElement, html} from 'lit';
import {styleMap} from 'lit/directives/style-map.js';

export class MyElement extends LitElement {
  static properties = {
    enabled: {type: Boolean},
    hidden: {type: Boolean},
  };

  constructor() {
    super();
    this.enabled = true;
    this.hidden = false;
  }

  render() {
    const styles = {
      backgroundColor: this.enabled ? 'lightgreen' : 'transparent',
      opacity: this.hidden ? '0.2' : '1',
      padding: '10px',
    };
    return html`
      <h3>styleMap directive example</h3>

      <p style=${styleMap(styles)}>Hello style!</p>
      <hr>
      <label>
        <input type="checkbox" .checked=${this.enabled} @change=${
      this.toggleEnabled
    }>
        Enabled
      </label>
      <label>
        <input type="checkbox" .checked=${this.hidden} @change=${
      this.toggleHidden
    }>
        Hidden
      </label>
    `;
  }

  toggleEnabled() {
    this.enabled = !this.enabled;
  }

  toggleHidden() {
    this.hidden = !this.hidden;
  }
}
customElements.define('my-element', MyElement);
