import {LitElement, css, html} from 'lit';

class MyElement extends LitElement {
  static styles = css`
      ::slotted(*) {
        border: 1px solid blue;
      	padding: 6px;
      }
    `;
  render() {
    return html`
      <div>
        <slot></slot>
      </div>
    `;
  }
}
customElements.define('my-element', MyElement);
