import {html, css, LitElement} from 'lit';

export class CommentCard extends LitElement {
  static properties = {
    subject: {},
    content: {},
    user: {},
    time: {},
    avatar: {},
  };

  static styles = css`
    :host {
      border: 1px solid #666;
      border-radius: 5px;
      background: #ddd;
      display: flex;
      font-family: sans-serif;
      padding: 5px;
      max-width: 500px;
    }
    .avatar {
      height: 40px;
      width: 40px;
      background: lightblue;
      border: 1px solid #666;
      border-radius: 20px;
    }
    .subject {
      font-weight: bold;
    }
    .usertime {
      white-space: nowrap;
    }
    .body {
      flex: 1;
      margin-inline-start: 10px;
    }
  `;

  constructor() {
    super();
    this.subject = 'Default title';
    this.content = 'Lorem ipsum dolor sit amet.';
    this.user = 'username';
    this.time = 'one minute ago';
    this.avatar = '//joeschmoe.io/api/v1/jess';
  }

  render() {
    return html`
      <img class="avatar" src=${this.avatar}>
      <div class="body">
        <div class="header">
          <span class="subject">${this.subject}</span>
          <span class="usertime">@${this.user} â€¢ ${this.time}</span>
        </div>
        <div class="content">${this.content}</div>
      </div>
    `;
  }
}
customElements.define('comment-card', CommentCard);
