date-display.js.content:
import {LitElement, html} from 'lit';

export class DateDisplay extends LitElement {
  static properties = {
    date: {attribute: false},
    dateStr: {type: String, attribute: 'date-str'},
  };

  constructor() {
    super();
    this.date = new Date();
    this.dateStr = '';
  }

  willUpdate(changed) {
    if (changed.has('dateStr') && this.dateStr) {
      this.date = new Date(this.dateStr);
    }
  }

  render() {
    const locale = 'en-US';
    const options = {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    };
    return html`
      <p>The given date is: ${this.date.toLocaleDateString(locale, options)}</p>
    `;
  }
}
customElements.define('date-display', DateDisplay);

