import {LitElement, html} from 'lit';

export class LightDom extends LitElement {
  render() {
    return html`
      <p>Custom rendering without shadow DOM (note, styling leaks in).</p>
    `;
  }
  createRenderRoot() {
    return this;
  }
}
customElements.define('light-dom', LightDom);
