import {html, css, LitElement} from 'lit';
import {customElement, property} from 'lit/decorators.js';

@customElement('comment-card')
export class CommentCard extends LitElement {

  static styles = css`
    :host {
      border: 1px solid #666;
      border-radius: 5px;
      background: #ddd;
      display: flex;
      font-family: sans-serif;
      padding: 5px;
      max-width: 500px;
    }
    .avatar {
      height: 40px;
      width: 40px;
      background: lightblue;
      border: 1px solid #666;
      border-radius: 20px;
    }
    .subject {
      font-weight: bold;
    }
    .usertime {
      white-space: nowrap;
    }
    .body {
      flex: 1;
      margin-inline-start: 10px;
    }
  `;

  @property() subject = 'Default title';
  @property() content = 'Lorem ipsum dolor sit amet.';
  @property() user = 'username';
  @property() time = 'one minute ago';
  @property() avatar = '//joeschmoe.io/api/v1/jess';

  render() {
    return html`
      <img class="avatar" src=${this.avatar}>
      <div class="body">
        <div class="header">
          <span class="subject">${this.subject}</span>
          <span class="usertime">@${this.user} â€¢ ${this.time}</span>
        </div>
        <div class="content">${this.content}</div>
      </div>
    `;
  }

}

time-ago.ts.content:
import {directive, Part, DirectiveParameters} from 'lit/directive.js';
import {AsyncDirective} from 'lit/async-directive.js';
import {format} from 'timeago.js';

class TimeAgoDirective extends AsyncDirective {

  timer: number | undefined;
  time!: Date;

  render(time: Date) {
    return format(time);
  }

  update(part: Part, [time]: DirectiveParameters<this>) {
    this.time = time;
    if (this.isConnected) {
      this.ensureTimerStarted();
    }
    return this.render(time);
  }

  ensureTimerStarted() {
    if (this.timer === undefined) {
      this.timer = setInterval(() => {
        this.setValue(this.render(this.time));
      }, 1000);
    }
  }

  ensureTimerStopped() {
    clearInterval(this.timer);
    this.timer = undefined;
  }

  disconnected() {
    this.ensureTimerStopped();
  }

  reconnected() {
    this.ensureTimerStarted();
  }

}

export const timeAgo = directive(TimeAgoDirective);

time-ago-example.ts.content:
/* playground-hide */
import {html, css, LitElement} from 'lit';
import {customElement, property} from 'lit/decorators.js';

import {timeAgo} from './time-ago.js';

import './comment-card.js';

const timeNow = new Date();

@customElement('time-ago-example')
export class TimeAgoExample extends LitElement {

  static styles = css`
    hr { margin: 20px 0; }
  `;

  @property() chosenTime = new Date('2020-03-10');

  /* playground-hide-end */
  render() {
    return html`
      <p>This page was rendered ${timeAgo(timeNow)}.</p>
      <hr>

      Select your last vacation:
      <input type="date" .valueAsDate=${this.chosenTime} @change=${this.updateTime}>
      <p>Your last vacation was ${timeAgo(this.chosenTime)}.</p>
      <hr>

      <comment-card user="litdeveloper"
                    time=${timeAgo(timeNow)}
                    subject="Just tried AsyncDirectives!"
                    content="I just tried out these AsyncDirectives in Lit and they're pretty powerful!">
      </comment-card>
    `;
  }
  /* playground-hide */

  updateTime(event: Event) {
    const target = event.target as HTMLInputElement;
    this.chosenTime = target.valueAsDate!;
  }

}
/* playground-hide-end */
